Use NewCentralUsers

SET NOCOUNT ON

DECLARE @sql NVARCHAR(MAX)

IF OBJECT_ID('tempdb..#TablesToCheck') IS NOT NULL
	DROP TABLE #TablesToCheck

CREATE TABLE #TablesToCheck
(
	SchemaName NVARCHAR(256) NOT NULL,
	TableName NVARCHAR(256) NOT NULL,
	ColumnName NVARCHAR(256) NULL,
	FromClause NVARCHAR(MAX) NULL,
	IgnoreReason NVARCHAR(255) NULL
)

IF (1=1)
BEGIN		
--		INSERT INTO #TablesToCheck(SchemaName, TableName, ColumnName, WhereClause) VALUES('dbo', 'tmpNCUSunbeltTest', 'NCUUserID', 'CONVERT(INT, [NCUUserID]) = @userID')
--		INSERT INTO #TablesToCheck(SchemaName, TableName, ColumnName, WhereClause) VALUES('dbo', 'tmpNCUSunbeltTest', 'UserID', 'CONVERT(INT, [UserID]) = @userID')
END

DECLARE @schemaName NVARCHAR(256), @tableName NVARCHAR(256), @columnName NVARCHAR(256), @fromClause NVARCHAR(MAX)

DECLARE curTables CURSOR FOR
SELECT SchemaName, TableName, ColumnName, FromClause
FROM #TablesToCheck
WHERE [IgnoreReason] IS NULL

OPEN curTables
FETCH NEXT FROM curTables INTO @schemaName, @tableName, @columnName, @fromClause

WHILE @@FETCH_STATUS = 0
BEGIN
	SET @sql = N'
	print ''' + @schemaName + ', ' + replace(@tableName, '''', '''''') + '''' + N'
	set @deleteCount = 4500
	while @deleteCount = 4500
	begin
		begin transaction
			delete top (4500) t1 from ' + @fromClause + N'
			select @deleteCount = @@rowcount
			print @deleteCount
		commit transaction with (delayed_durability = on)
	end	'

	PRINT @sql

	FETCH NEXT FROM curTables INTO @schemaName, @tableName, @columnName, @fromClause
END   

CLOSE curTables
DEALLOCATE curTables

